import React from 'react'

class WorldClock extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        error: null,
        isLoaded: false,
        date: new Date()
      };
    }
    componentDidMount() {
      this.timerID = setInterval(
        () => this.tick(),
        1000
      );
    }
  
    tick() {
      fetch("https://http://worldtimeapi.org/api/timezone/America/Fortaleza")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            error: null,
            isLoaded: true,
            date: new Date (result.datetiime)      
          });
        },
        // Nota: É importante lidar com os erros aqui
        // em vez de um bloco catch() para não recebermos
        // exceções de erros dos componentes.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
      //this.setState({
      //  date: new Date(),
      //  isLoaded: true
      //});
      //this.state = {date: new Date};
    }
  
    render() {
      let {date, isLoaded, error} = this.state;
      if (error){
        return(
          <div>
              <h2>World clock :/ ({error})</h2>
          </div>
        )
      } else if (!isLoaded){
          return(
            <div>
                <h2>World clock is starting...</h2>
            </div>
          );
      } else {
          return (
            <div>
                <h2>World clock is {date.toLocaleTimeString()}.</h2>
            </div>
      );
      } 
    }
  }

  export default WorldClock;
